// Generate realistic soil moisture data with EMA smoothing
const min = 30;
const max = 80;
const variance = 5;

// Get previous value from context or initialize
let prevValue = context.get('prevValue') || 55;

// Generate new value with random walk
const randomChange = (Math.random() - 0.5) * variance * 2;
const targetValue = prevValue + randomChange;

// Apply EMA smoothing (alpha = 0.3)
const emaAlpha = 0.3;
const smoothedValue = emaAlpha * targetValue + (1 - emaAlpha) * prevValue;

// Clamp to min/max
const value = Math.max(min, Math.min(max, smoothedValue));

// Store for next iteration
context.set('prevValue', value);

// Determine status
let status = 'optimal';
if (value < 40 || value > 70) {
    status = value < 35 || value > 75 ? 'critical' : 'warning';
}

msg.payload = {
    sensor: 'Soil Moisture',
    value: Math.round(value * 10) / 10,
    unit: '%',
    status: status,
    timestamp: new Date().toISOString(),
    optimal: { min: 40, max: 70 }
};

return msg;

