// Generate realistic water flow data
const min = 0;
const max = 10;
const variance = 0.5;

let prevValue = context.get('prevValue') || 3;
const randomChange = (Math.random() - 0.5) * variance * 2;
const targetValue = prevValue + randomChange;

const emaAlpha = 0.3;
const smoothedValue = emaAlpha * targetValue + (1 - emaAlpha) * prevValue;
const value = Math.max(min, Math.min(max, smoothedValue));

context.set('prevValue', value);

let status = 'optimal';
if (value < 2 || value > 5) {
    status = value < 1 || value > 6 ? 'critical' : 'warning';
}

msg.payload = {
    sensor: 'Water Flow',
    value: Math.round(value * 10) / 10,
    unit: 'L/min',
    status: status,
    timestamp: new Date().toISOString(),
    optimal: { min: 2, max: 5 }
};

return msg;

// Collect sensor data and make intelligent decisions
const sensorData = context.get('sensorData') || {};

// Store incoming sensor data
if (msg.payload && msg.payload.sensor) {
    sensorData[msg.payload.sensor] = msg.payload;
    context.set('sensorData', sensorData);
}

// Only make decision if we have all sensor data
const requiredSensors = ['Soil Moisture', 'Temperature', 'Humidity', 'Light Intensity', 'COâ‚‚ Level', 'Water Flow'];
const hasAllData = requiredSensors.every(sensor => sensorData[sensor]);

if (!hasAllData) {
    return null;
}

// Analyze sensor data and make decision
const criticalSensors = Object.values(sensorData).filter(s => s.status === 'critical');
const warningSensors = Object.values(sensorData).filter(s => s.status === 'warning');

let decision = {
    timestamp: new Date().toISOString(),
    issue: 'System Normal',
    decision: 'Continue monitoring',
    rationale: 'All sensors within acceptable ranges',
    action: 'MAINTAIN',
    severity: 'success',
    confidence: 0.95,
    sensorValues: {}
};

// Build sensor values summary
Object.values(sensorData).forEach(sensor => {
    decision.sensorValues[sensor.sensor] = `${sensor.value}${sensor.unit}`;
});

// Critical decision logic
if (criticalSensors.length > 0) {
    const sensor = criticalSensors[0];
    decision.severity = 'critical';
    decision.confidence = 0.88;

    if (sensor.sensor === 'Soil Moisture' && sensor.value < sensor.optimal.min) {
        decision.issue = 'CRITICAL: Low Soil Moisture';
        decision.decision = 'Activate irrigation immediately';
        decision.rationale = `Soil moisture at ${sensor.value}% is critically below optimal range. Immediate irrigation required to prevent crop stress.`;
        decision.action = 'AUTO_IRRIGATE_HIGH';

        // Trigger irrigation actuator
        node.send([null, { payload: { actuator: 'irrigation', command: 'on', intensity: 80 } }]);
    } else if (sensor.sensor === 'Temperature' && sensor.value > sensor.optimal.max) {
        decision.issue = 'CRITICAL: High Temperature';
        decision.decision = 'Activate cooling and ventilation';
        decision.rationale = `Temperature at ${sensor.value}Â°C exceeds safe threshold. Risk of heat stress.`;
        decision.action = 'AUTO_VENTILATION_HIGH';

        // Trigger ventilation actuator
        node.send([null, { payload: { actuator: 'ventilation', command: 'on', intensity: 100 } }]);
    } else if (sensor.sensor === 'COâ‚‚ Level' && sensor.value > sensor.optimal.max) {
        decision.issue = 'CRITICAL: High COâ‚‚ Level';
        decision.decision = 'Emergency ventilation';
        decision.rationale = `COâ‚‚ at ${sensor.value}ppm approaching dangerous levels. Immediate ventilation required.`;
        decision.action = 'EMERGENCY_VENT';

        // Trigger emergency ventilation
        node.send([null, { payload: { actuator: 'ventilation', command: 'on', intensity: 100 } }]);
    }
} else if (warningSensors.length > 0) {
    const sensor = warningSensors[0];
    decision.severity = 'warning';
    decision.confidence = 0.82;
    decision.issue = `WARNING: ${sensor.sensor} approaching limits`;
    decision.decision = 'Prepare corrective measures';
    decision.rationale = `${sensor.sensor} at ${sensor.value}${sensor.unit} is outside optimal range but not critical yet.`;
    decision.action = 'STANDBY_MONITOR';
}

msg.payload = decision;

// Return decision to display and actuator control
return [msg, null];

// Control actuators based on decisions
if (!msg.payload || !msg.payload.actuator) {
    return null;
}

const { actuator, command, intensity } = msg.payload;

const actuatorMsg = {
    payload: {
        name: actuator,
        command: command,
        intensity: intensity || 0,
        timestamp: new Date().toISOString()
    }
};

// Route to appropriate actuator
if (actuator === 'irrigation') {
    return [actuatorMsg, null, null, null];
} else if (actuator === 'ventilation') {
    return [null, actuatorMsg, null, null];
} else if (actuator === 'lighting') {
    return [null, null, actuatorMsg, null];
} else if (actuator === 'heating') {
    return [null, null, null, actuatorMsg];
}

return null;

// Format data for CSV export
const timestamp = new Date().toISOString();
const sensors = msg.payload;

// Get decision from context
const decision = context.get('lastDecision') || {
    issue: 'Initializing',
    decision: 'System starting',
    rationale: 'Collecting initial data',
    action: 'INIT'
};

// Build sensor values string
const sensorValues = Object.keys(sensors)
    .map(key => `${key}: ${sensors[key].value}${sensors[key].unit}`)
    .join(', ');

msg.payload = {
    Time: timestamp,
    Issue: decision.issue,
    'Sensor Values': sensorValues,
    Decision: decision.decision,
    Rationale: decision.rationale,
    Action: decision.action
};

return msg;

// Store decision for CSV logging
context.set('lastDecision', msg.payload);
return msg;

// Trigger CSV file download
msg.payload = {
    filename: 'edgeagrochain-history.csv',
    path: '/data/edgeagrochain-history.csv'
};
return msg;
